import React, {Component} from "react"
import {Route} from "react-router-dom"
import TopNavigation from "./TopNavigation"
import {Async, lazyImport} from "./LazyLoad"

const SignUpPage = Async(lazyImport("./pages/SignUpPage"))
const HomePage = Async(lazyImport("./pages/HomePage"))
const LogInPage = Async(lazyImport("./pages/LogInPage"))
const FilmsPage = Async(lazyImport("/pages/FilmsPage"))
const FilmDetailsPage = Async(lazyImport("/pages/FilmDetailsPage"))

export class App extends Component {
  state = {
    user: {
      token: "test",
      role: "user",
    },
  }

  logout = () => {
    this.setState({user: {token: null, role: "user"}})
  }

  render() {
    const {user} = this.state
    return (
      <div className="ui container pt-3">
        <TopNavigation isAuth={user.token !== null} logout={this.logout} />

        <Route exact path="/" component={HomePage} />
        <Route path="/signup" component={SignUpPage} />
        <Route path="/login" component={LogInPage} />
        <Route path="/films" component={FilmsPage} />
        <Route path="/films/details" component={FilmDetailsPage} />
      </div>
    )
  }
}

export default App
