import React from "react"
import FormMessage from "./FormMessage"
import {Link} from "react-router-dom"
import moduleName from '/l'

const initialData = {
  email: "",
  password: "",
  passwordConfirm: "",
}

class SignUpForm extends React.Component {
  state = {
    data: initialData,
    errors: {},
    isLoading: false,
  }
  handleTextInputChange = ({target}) =>
    this.setState({
      data: {...this.state.data, [target.name]: target.value},
      errors: {...this.state.errors, [target.name]: ""},
    })

  validate = ({email, password, passwordConfirm}) => {
    const errors = {}

    if (!email) errors.email = "Email field is required"
    if (!password && password.length < 6)
      errors.password = "Password must contain at least 6 character"

    if (passwordConfirm !== password || !passwordConfirm.length)
      errors.passwordConfirm = "The password do not match"

    return errors
  }

  handleSubmit = e => {
    e.preventDefault()
    const {data} = this.state
    const errors = this.validate(data)
    this.setState({errors})

    if (Object.keys(errors).length === 0) {
      this.setState({isLoading: true})
      this.props
        .submit(data)
        .catch(err =>
          this.setState({errors: err.response.data.errors, isLoading: false}),
        )
    }
  }

  render() {
    const {data, errors, isLoading} = this.state
    const loadClass = isLoading ? "loading" : ""
    return (
      <form className={`ui form  ${loadClass}`} onSubmit={this.handleSubmit}>
        <h1 className="ui center">Register Now</h1>
        <div className={errors.email ? "error field" : "field"}>
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="E-mail address"
            value={data.email}
            onChange={this.handleTextInputChange}
          />
          <FormMessage>{errors.email}</FormMessage>
        </div>

        <div className={errors.password ? "error field" : "field"}>
          <label>Password</label>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={data.password}
            onChange={this.handleTextInputChange}
          />
          <FormMessage>{errors.password}</FormMessage>
        </div>
        <div className={errors.passwordConfirm ? "error field" : "field"}>
          <label>Password Confirm</label>
          <input
            type="password"
            name="passwordConfirm"
            placeholder="Password Confirm"
            value={data.passwordConfirm}
            onChange={this.handleTextInputChange}
          />
          <FormMessage>{errors.passwordConfirm}</FormMessage>
        </div>
        <div className="small ui fluid buttons">
          <button className="ui green button">OK</button>
          <Link to="/" className="ui red button">
            CANCEL
          </Link>
        </div>
      </form>
    )
  }
}

export default SignUpForm
