import React from "react"
import {Link} from "react-router-dom"
import FormMessage from "./FormMessage"

const initialData = {
  email: "",
  password: "",
  isLoading: false,
}

class LoginForm extends React.Component {
  state = {
    data: initialData,
    errors: {},
  }
  handleTextInputChange = ({target}) =>
    this.setState({
      data: {...this.state.data, [target.name]: target.value},
      errors: {...this.state.errors, [target.name]: ""},
    })

  handleSubmit = e => {
    const {data} = this.state
    e.preventDefault()
    const errors = this.validate(data)
    this.setState({errors})
    if (Object.keys(errors).length === 0) {
      this.setState({isLoading: true})
      this.props
        .submit(data)
        .catch(error =>
          this.setState({errors: error.response.data.errors, isLoading: false}),
        )
    }
  }

  validate(data) {
    const errors = {}
    if (!data.email) errors.email = "Email cannot be blank"
    if (!data.password) errors.password = "Password cannot be blank"

    return errors
  }
  render() {
    const {data, errors, isLoading} = this.state
    const loadClass = isLoading ? "loading" : ""
    return (
      <div className="ui container grid center">
        <form
          class={`ui form eight wide column ${loadClass}`}
          onSubmit={this.onSubmit}
        >
          <h1>Login</h1>
          <div class="ui stacked segment">
            <div class="field">
              <div class="ui left icon input">
                <i class="user icon"></i>
                <input
                  type="text"
                  name="email"
                  placeholder="E-mail address"
                  value={data.email}
                  onChange={this.handleTextInputChange}
                />
                <FormMessage>{errors.email}</FormMessage>
              </div>
            </div>
            <div class="field">
              <div class="ui left icon input">
                <i class="lock icon"></i>
                <input type="password" name="password" placeholder="Password" />
                <FormMessage>{errors.password}</FormMessage>
              </div>
            </div>
            <button class="ui fluid large teal submit button">Login</button>
          </div>

          <div class="ui error message"></div>
        </form>
      </div>
    )
  }
}

export default LoginForm
