import React from "react"
import FormMessage from "./FormMessage"

class RegistrationForm extends React.Component {
  state = {
    email: "",
    password: "",
    passwordConfirm: "",
    errors: {},
  }
  handleTextInputChange = ({target}) =>
    this.setState({
      [target.name]: target.value,
      errors: {...this.state.errors, [target.name]: ""},
    })

  validate = ({email, password, passwordConfirm}) => {
    const errors = {}

    if (!email) errors.email = "Email field is required"
    if (!password) errors.password = "Password field is required"

    if (!password.length && password.length < 6)
      errors.password = "Password must contain at least 6 character"

    if (passwordConfirm !== password && !passwordConfirm.length)
      errors.passwordConfirm = "The password do not match"

    return errors
  }

  handleSubmit = e => {
    const {email, password, passwordConfirm} = this.state
    e.preventDefault()
    const errors = this.validate({email, password, passwordConfirm})
    this.setState({errors})
  }

  render() {
    const {email, password, passwordConfirm, errors} = this.state
    return (
      <div className="ui container grid center aligned">
        <form
          className="ui form eight wide column"
          onSubmit={this.handleSubmit}
        >
          <h1 className="ui center">Register Now</h1>
          <div className="field">
            <label>Email</label>
            <input
              type="text"
              name="email"
              placeholder="E-mail address"
              value={email}
              onChange={this.handleTextInputChange}
            />
            <FormMessage>{errors.email}</FormMessage>
          </div>
          <div className="field">
            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={this.handleTextInputChange}
            />
            <FormMessage>{errors.password}</FormMessage>
          </div>
          <div className="field">
            <label>Password Confirm</label>
            <input
              type="password"
              name="passwordConfirm"
              placeholder="Password Confirm"
              value={passwordConfirm}
              onChange={this.handleTextInputChange}
            />
            <FormMessage>{errors.passwordConfirm}</FormMessage>
          </div>
          <div className="small ui two buttons">
            <button className="ui green button">OK</button>
            <button className="ui red button">CANCEL</button>
          </div>
        </form>
      </div>
    )
  }
}

export default RegistrationForm
