import React from "react"
import FormMessage from "./FormMessage"

const initialData = {
  email: "",
  password: "",
  isLoading: false,
}

class LoginForm extends React.Component {
  state = {
    data: initialData,
    errors: {},
  }

  handleTextInputChange = ({target}) =>
    this.setState({
      data: {...this.state.data, [target.name]: target.value},
      errors: {...this.state.errors, [target.name]: ""},
    })

  handleSubmit = e => {
    e.preventDefault()
    const {data} = this.state
    const errors = this.validate(data)
    this.setState({errors})
    if (Object.keys(errors).length === 0) {
      this.setState({isLoading: true})
      this.props
        .submit(data)
        .catch(error =>
          this.setState({errors: error.response.data.errors, isLoading: false}),
        )
    }
  }

  validate(data) {
    const errors = {}
    if (!data.email) errors.email = "Email cannot be blank"
    if (!data.password) errors.password = "Password cannot be blank"

    return errors
  }
  render() {
    const {data, errors, isLoading} = this.state
    const loadClass = isLoading ? "loading" : ""
    return (
      <form className={`ui form ${loadClass}`} onSubmit={this.handleSubmit}>
        <h1>Login</h1>
        <div className="ui stacked segment">
          <div className={errors.email ? "error field" : "field"}>
            <div className="ui left icon input">
              <i className="user icon"></i>
              <input
                type="email"
                name="email"
                placeholder="E-mail address"
                value={data.email}
                onChange={this.handleTextInputChange}
              />
            </div>
          </div>
          <div className={errors.password ? "error field" : "field"}>
            <div className="ui left icon input">
              <i className="lock icon"></i>
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={data.password}
                onChange={this.handleTextInputChange}
              />
            </div>
          </div>
          <button className="ui fluid large teal button">Login</button>
        </div>

        <div className="ui error message"></div>
      </form>
    )
  }
}

export default LoginForm
