import React, {Component} from "react"
import {Route} from "react-router-dom"
import _orderBy from "lodash/orderBy"
import _find from "lodash/find"
import FilmsList from "./films"
import FilmForm from "./forms/FilmForm"
import FilmContext from "./context/FilmContext"
import api from "../api"
import Spinner from "./spinner/Spinner"

export class App extends Component {
  state = {
    films: [],
    selectedFilm: {},
    isLoading: true,
  }

  componentDidMount() {
    api.films.fetchAll().then(films =>
      this.setState({
        films: this.sortFilms(films),
        isLoading: false,
      }),
    )
  }

  sortFilms = films => _orderBy(films, ["featured", "title"], ["desc", "asc"])

  toggleFeatured = id => e => {
    const film = _find(this.state.films, {_id: id})
    this.updateFilm({...film, featured: !film.featured})
  }

  saveFilm = film =>
    film._id === null ? this.addFilm(film) : this.updateFilm(film)

  addFilm = filmData =>
    api.films.create(filmData).then(film =>
      this.setState(({films}) => ({
        films: this.sortFilms([...films, film]),
      })),
    )

  updateFilm = filmData =>
    api.films.update(filmData).then(film =>
      this.setState(({films}) => ({
        films: this.sortFilms(films.map(f => (f._id === film._id ? film : f))),
      })),
    )

  selectFilmForEdit = selectedFilm => () =>
    this.setState({
      selectedFilm,
      showAddForm: true,
    })

  deleteFilm = film => e =>
    api.films.delete(film).then(() => {
      this.setState(({films}) => ({
        films: this.sortFilms(films.filter(f => f._id !== film._id)),
      }))
      this.hideForm()
    })

  render() {
    const {films, selectedFilm, isLoading} = this.state
    const cols = showAddForm ? "ten" : "sixteen"
    return (
      <FilmContext.Provider
        value={{
          toggleFeatured: this.toggleFeatured,
          selectFilmForEdit: this.selectFilmForEdit,
          deleteFilm: this.deleteFilm,
        }}
      >
        <div className="ui stackable grid">
          {showAddForm && (
            <div className="six wide column">
              <FilmForm
                hideForm={this.hideForm}
                saveFilm={this.saveFilm}
                film={selectedFilm}
              />
            </div>
          )}
          {isLoading ? (
            <Spinner />
          ) : (
            <div className={`${cols} wide column`}>
              <FilmsList films={films} />
            </div>
          )}
        </div>
      </FilmContext.Provider>
    )
  }
}

export default App
